//import logo from './logo.svg';
import { useState } from 'react'
import './App.css';

const QuestionLists1 = 
{
  Question1: '1.早稲田出身の選手は?',
  Answers1: ['大迫傑選手', '服部勇馬選手', '中村匠吾選手', '相澤晃選手'],
  Correct1: '大迫傑選手',
  selectedAnswer1: ''
}

function App() {
  const [qLIsts1, setqLists1] = useState(QuestionLists1)

  const inputAnswer1 = (selectedAnswer1, targetIndex1) => {
    setqLists1(qLIsts1.map((list, index) => (index === targetIndex1 ? { ...list, selectedAnswer1 } : list)))
  }

  return (
    <div>
      {qLIsts1.map(({ Question1, Answers1, Correct1, selectedAnswer1 }, index) => {
        return (
          <div key={Question1}>
            <h1>{Question1}</h1>
            <div>
              {Answers1.map((answer) => {
                return (
                  <button key={answer} onClick={() => inputAnswer1(answer, index)}>
                    {answer}
                  </button>
                )
              })}
            </div>
            {selectedAnswer1 && <div>{Correct1 === selectedAnswer1 ? '正解' : '不正解'}</div>}
          </div>
        )
      })}
    </div>
  )
}

export default App;
