import React from "react";
import { useLocation } from "react-router-dom";
import logo from '../../images/exhibition_logo.png';

function Header(){

  let location = useLocation();
  let innerHeader = ''
  let Headerttl = ''
  const headerConts = [
    {
      ttl: "ボーボボクイズ",
      unCollect: "",
      messa: '掲載されている全ての画像の無断での<br className="sp_style" />使用・転載を固くお断りいたします。'
    },
    {
      ttl: "全問正解！<br />おめでとう！",
      unCollect: "",
    },
    {
      ttl: "残念！",
      unCollect: "番目が正解です",
    },
    {
      ttl: "",
      unCollect: "",
    },
  ]

  const pathRoot = "/"
  const pathCongrats = "/congrats"
  const pathAgain = "/again"


  if(location.pathname === pathRoot){
    innerHeader = "inner-header yellow"
  }else if(location.pathname === pathCongrats){
    innerHeader = "inner-header orrange"
  }else if(location.pathname === pathAgain){
    innerHeader = "inner-header blue"
  }else{
    innerHeader = "inner-header gray"
  }

  return(
    <header className="bg-white">
      <div className={innerHeader}>
        <h1>
          <img src={logo} className="App-logo" alt="ボボボーボ・ボーボボ展　ハジケ伝説よ　永遠に" />
        </h1>
        <h2>
          ボーボボクイズ
        </h2>
        <p className="errorMessage">

        </p>
        <p className="subTitle">
        全問正解した人には素敵なプレゼント<br />があるかも！？
        </p>
        <p className="text-danger">
        掲載されている全ての画像の無断での<br className="sp_style" />使用・転載を固くお断りいたします。
        </p>
        <div className="text-center">

        </div>
      </div>
    </header>
  );
}

export default Header;
