      style: "inner-header blue",
      ttl: "残念！",
      errorMessa: "",
      subTtl: "",
      unCollect: "番目が正解です",
      messa: ""
    },
    {
      style: "inner-header gray",
      ttl: "",
      errorMessa: "",
      subTtl: "",
      unCollect: "",
      messa: ""
