          <img src={logo} className="App-logo" alt="ボボボーボ・ボーボボ展　ハジケ伝説よ　永遠に" />
        </h1>
        <h2>{headerTitle}</h2>
        <p className="unCollect">{headerUnCollect}</p>
        <p className="errorMessage">{errorMessage}</p>
        <p className="subTitle">{headerSubTitle}</p>
        <p className="text-danger">{headerMessage}</p>
        <div className="text-center">

        </div>
      </div>
    </header>
  );
}

export default Header;
