import React, {useState} from 'react';
import { Col, Container, Row, Form, FormGroup, Label, Input, Button } from 'reactstrap';
import Header from './common/Header';
import Footer from './common/Footer';
import { useHistory } from "react-router-dom";
import boboicon from '../images/icon_bomb.png';
import A1img from '../images/A1.png';
import A2img from '../images/A2.png';
import A3img from '../images/A3.png';
import A4img from '../images/A4.png';

const data = [
  {
    name: "第1問",
    question: "突然現れた胃液ガールズ！レモンプリンセス、イチゴプリンセス、もう一人は？",
    source: "※出典(コミックス第7巻：奥義71／アニメ第28話より)",
    answer: [
      { name: "メロンプリンセス", judgement: true },
      { name: "Ms.メロン", judgement: false },
      { name: "メロン子", judgement: false }
    ]
  },
  {
    name: "第2問",
    question: "ダイナマイト3兄弟にハジケの勝負を挑んだボーボボ！初めての株でボーボボが投資した金額はいくら？",
    source: "※出典(コミックス第１巻：奥義３／アニメ第3話より)",
    answer: [
      { name: "5万", judgement: true },
      { name: "5億", judgement: false },
      { name: "5兆", judgement: false }
    ]
  },
  {
    name: "第3問",
    question: "ボーボボが校庭でクマと練習していたのは「プルコギ」ですが、次のテストは何でしょうか？",
    source: "※出典(アニメ第4話より)",
    answer: [
      { name: "ビビンバ", judgement: true },
      { name: "レーメン", judgement: false },
      { name: "クッパ", judgement: false }
    ]
  },
  {
    name: "第4問",
    question: "突然現れた軍艦5人衆！全部で何人いる？",
    source: "※出典(コミックス第4巻：奥義39／アニメ第18話より)",
    answer: [
      { name: "5人", judgement: true },
      { name: "6人", judgement: false },
      { name: "32人", judgement: false }
    ]
  }
]



const Home = (props) => {
  const [radio1, setRadio1] = useState("メロンプリンセス")
  const [radio2, setRadio2] = useState("5万")
  const [radio3, setRadio3] = useState("ビビンバ")
  const [radio4, setRadio4] = useState("5人")

  let history = useHistory();
  function selectCheck(){
    if(radio1 === answer[0] && radio2 === answer[1] && radio3 === answer[2] && radio4 === answer[3]){
      history.push("/congrats")
    }else{
      history.push("/again")
    }
  }

  return(
    <>
      <Container className="p-4 bg yellow">
        <Header />

        <main className="bg-white my-3">
          <Form>

            {/* 第一問 */}
            <div className="mb-4">
              <h3 className="ttlNum mb-3"><img src={boboicon} className="Bobo-icon" alt="icon" />{data[0].name}</h3>
              <Row
                sm="2"
                xs="1"
              >
                <Col>
                  <FormGroup tag="fieldset" className="q-set">
                    <legend>
                      <p>{data[0].question}</p>
                      <p className="t-normal">{data[0].source}</p>
                    </legend>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "メロンプリンセス"}
                        value="メロンプリンセス"
                        onChange={(e1)=>{ setRadio1(e1.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                      メロンプリンセス
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "Ms.メロン"}
                        value="Ms.メロン"
                        onChange={(e1)=>{ setRadio1(e1.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                      Ms.メロン
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "メロン子"}
                        value="メロン子"
                        onChange={(e1)=>{ setRadio1(e1.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                      メロン子
                      </Label>
                    </FormGroup>
                  </FormGroup>
                </Col>
                <Col className="t-right">
                  <img src={A1img} className="qimage" alt="" />
                </Col>
              </Row>
            </div>

            {/* 第二問 */}
            <div className="mb-4">
              <h3 className="ttlNum mb-3"><img src={boboicon} className="Bobo-icon" alt="icon" />{data[1].name}</h3>
              <Row
                sm="2"
                xs="1"
              >
                <Col>
                  <FormGroup tag="fieldset" className="q-set">
                    <legend>
                      <p>{data[1].question}</p>
                      <p className="t-normal">{data[1].source}</p>
                    </legend>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio2 === "5万"}
                        value="5万"
                        onChange={(e2)=>{ setRadio2(e2.target.value) }}
                        name="radioGroup2"
                      />
                      {' '}
                      <Label>
                      5万
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio2 === "5億"}
                        value="5億"
                        onChange={(e2)=>{ setRadio2(e2.target.value) }}
                        name="radioGroup2"
                      />
                      {' '}
                      <Label>
                      5億
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio2 === "5兆"}
                        value="5兆"
                        onChange={(e2)=>{ setRadio2(e2.target.value) }}
                        name="radioGroup2"
                      />
                      {' '}
                      <Label>
                      5兆
                      </Label>
                    </FormGroup>
                  </FormGroup>
                </Col>
                <Col className="t-right">
                  <img src={A2img} className="qimage" alt="" />
                </Col>
              </Row>
            </div>

            {/* 第三問 */}


            {/* 第四問 */}


            <Button 
            className="b-kaitou"
            onClick={selectCheck}
            >
            回答する
            </Button>

          </Form>
        </main>

        <Footer />
      </Container>
    </>
  );
}

export default Home;
