import React, {useState} from 'react';
import { Col, Container, Row, Form, FormGroup, Label, Input, Button } from 'reactstrap';
import Header from './common/Header';
import boboicon from '../images/icon_bomb.png';
import A1img from '../images/A1.png';

const Home = () => {
  const [radio, setRadio] = useState(true)
  const toggle = () => {
    setRadio(prevState => !prevState)
  }

  return(
    <>
      <Container className="p-4 bg yellow">
        <Header />

        <main className="bg-white my-3">
          <Form>

            {/* 第一問 */}
            <div className="mb-4">
              <h3 className="ttlNum mb-3"><img src={boboicon} className="Bobo-icon" alt="icon" />第1問</h3>
              <Row
                sm="2"
                xs="1"
              >
                <Col>
                  <FormGroup tag="fieldset" className="q-set">
                    <legend>
                      <p>突然現れた胃液ガールズ！レモンプリンセス、イチゴプリンセス、もう一人は？</p>
                      <p className="t-normal">※出典(コミックス第7巻：奥義71／アニメ第28話より)</p>
                    </legend>
                    <FormGroup>
                      <Input
                        onChange={toggle}
                        checked={radio}
                        name="radio1"
                        type={'radio'}
                      />
                      {' '}
                      <Label>
                        メロンプリンセス
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        onChange={toggle}
                        checked={!radio}
                        name="radio1"
                        type={'radio'}
                      />
                      {' '}
                      <Label>
                        Ms.メロン
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        onChange={toggle}
                        checked={!radio}
                        name="radio1"
                        type={'radio'}
                      />
                      {' '}
                      <Label>
                        メロン子
                      </Label>
                    </FormGroup>
                  </FormGroup>
                </Col>
                <Col className="t-right">
                  <img src={A1img} className="qimage" alt="" />
                </Col>
              </Row>
            </div>

            {/* 第二問 */}
            {/* 第三問 */}
            {/* 第四問 */}

            <Button className="b-kaitou">
            回答する
            </Button>

          </Form>
        </main>

        <footer>
          <Row
            sm="2"
            xs="1"
            className="text-center"
          >
            <Col>
              <a href="{}">プライバシーポリシー</a>
            </Col>
            <Col>
              <small>©澤井啓夫／集英社・東映アニメーション</small>
            </Col>
          </Row>
        </footer>
      </Container>
    </>
  );
}

export default Home;
