import React, {useState} from 'react';
import { Col, Row, FormGroup, Label, Input } from 'reactstrap';
import boboicon from '../images/icon_bomb.png';

function Quiz({id, name, question, source, answer, image}){

  const [radio1, setRadio] = useState("")
  return(
      <div className="mb-4">
        <h3 className="ttlNum mb-3"><img src={boboicon} className="Bobo-icon" alt="icon" />{name}</h3>
        <Row
          sm="2"
          xs="1"
        >
          <Col>
            <FormGroup tag="fieldset" className="q-set">
              <legend>
                <p>{question}</p>
                <p className="t-normal">{source}</p>
              </legend>
              <FormGroup>
                <Input
                  type={'radio'}
                  checked={radio1 === answer[0].name}
                  value={answer[0].name}
                  onChange={(e)=>{ setRadio(e.target.value) 
                  console.log(e.target.value) }}
                  name={id}
                />
                {' '}
                <Label>
                {answer[0].name}
                </Label>
              </FormGroup>
              <FormGroup>
                <Input
                  type={'radio'}
                  checked={radio1 === answer[1].name}
                  value={answer[1].name}
                  onChange={(e)=>{ setRadio(e.target.value) }}
                  name={id}
                />
                {' '}
                <Label>
                {answer[1].name}
                </Label>
              </FormGroup>
              <FormGroup>
                <Input
                  type={'radio'}
                  checked={radio1 === answer[2].name}
                  value={answer[2].name}
                  onChange={(e)=>{ setRadio(e.target.value) }}
                  name="radioGroup1"
                />
                {' '}
                <Label>
                {answer[2].name}
                </Label>
              </FormGroup>
            </FormGroup>
          </Col>
          <Col className="t-right">
            <img src={image} className="qimage" alt="" />
          </Col>
        </Row>
      </div>
  );
}


export default Quiz;
