//import logo from './logo.svg';
import { useState } from 'react'
import './App.css';

const QuestionLists = [
  {
    Question: '1.早稲田出身の選手は?',
    Answers: ['大迫傑選手', '服部勇馬選手', '中村匠吾選手', '相澤晃選手'],
    Correct: '大迫傑選手',
    selectedAnswer: ''
  },
  {
    Question: '2.東洋出身の選手は?',
    Answers: ['大迫傑選手', '服部勇馬選手', '中村匠吾選手', '相澤晃選手'],
    Correct: '服部勇馬選手',
    selectedAnswer: ''
  },
  {
    Question: '3.駒澤出身の選手は?',
    Answers: ['大迫傑選手', '服部勇馬選手', '中村匠吾選手', '相澤晃選手'],
    Correct: '中村匠吾選手',
    selectedAnswer: ''
  }
]

function App() {
  const [qLIsts, setqLists] = useState(QuestionLists)

  // console.log(setqLists)

  const inputAnswer = (selectedAnswer, targetIndex) => {
    setqLists(qLIsts.map((list, index) => (index === targetIndex ? { ...list, selectedAnswer } : list)))
  }

  // console.log(inputAnswer.selectedAnswer)

  return (
    <div>
      {qLIsts.map(({ Question, Answers, Correct, selectedAnswer }, index) => {
        return (
          <div key={Question}>
            <h1>{Question}</h1>
            <div>
              {Answers.map((answer) => {
                return (
                  <input type="radio" key={answer} value={() => inputAnswer(answer, index)} />
                )
              })}
            </div>
            {console.log(selectedAnswer)}
          </div>
        )
      })}
    </div>
  )
}

export default App;
