import React, {useState} from 'react';
import { Col, Container, Row, Form, FormGroup, Label, Input, Button } from 'reactstrap';
import Header from './common/Header';
import Footer from './common/Footer';
import { useHistory } from "react-router-dom";
import boboicon from '../images/icon_bomb.png';
import A1img from '../images/A1.png';

const data = [
  {
    name: "第1問",
    question: "突然現れた胃液ガールズ！レモンプリンセス、イチゴプリンセス、もう一人は？",
    source: "※出典(コミックス第7巻：奥義71／アニメ第28話より)",
    answer: [
      { name: "メロンプリンセス", judgement: true },
      { name: "Ms.メロン", judgement: false },
      { name: "メロン子", judgement: false }
    ],
    image: "../images/A1.png"
  },
  {
    name: "第2問",
    question: "ダイナマイト3兄弟にハジケの勝負を挑んだボーボボ！初めての株でボーボボが投資した金額はいくら？",
    source: "※出典(コミックス第１巻：奥義３／アニメ第3話より)",
    answer: [
      { name: "5万", judgement: true },
      { name: "5億", judgement: false },
      { name: "5兆", judgement: false }
    ],
    image: "../images/A2.png"
  },
  {
    name: "第3問",
    question: "ボーボボが校庭でクマと練習していたのは「プルコギ」ですが、次のテストは何でしょうか？",
    source: "※出典(アニメ第4話より)",
    answer: [
      { name: "ビビンバ", judgement: true },
      { name: "レーメン", judgement: false },
      { name: "クッパ", judgement: false }
    ],
    image: "../images/A3.png"
  },
  {
    name: "第4問",
    question: "突然現れた軍艦5人衆！全部で何人いる？",
    source: "※出典(コミックス第4巻：奥義39／アニメ第18話より)",
    answew: [
      { name: "5人", judgement: true },
      { name: "6人", judgement: false },
      { name: "32人", judgement: false }
    ],
    image: "../images/A4.png"
  }
]



const Home = (props) => {
  const [radio1, setRadio1] = useState("メロンプリンセス")
  const [radio2, setRadio2] = useState("5万")
  const [radio3, setRadio3] = useState("ビビンバ")
  const [radio4, setRadio4] = useState("5人")
  const answer = [ "メロン子", "5億", "ビビンバ", "6人" ]

  let history = useHistory();
  function selectCheck(){
    if(radio1 === answer[0]){
      history.push("/congrats")
    }else{
      history.push("/again")
    }
  }

  return(
    <>
      <Container className="p-4 bg yellow">
        <Header />

        <main className="bg-white my-3">
          <Form>

            {/* 第一問 */}
            <div className="mb-4">
              <h3 className="ttlNum mb-3"><img src={boboicon} className="Bobo-icon" alt="icon" />{data[0].name}</h3>
              <Row
                sm="2"
                xs="1"
              >
                <Col>
                  <FormGroup tag="fieldset" className="q-set">
                    <legend>
                      <p>{data[0].question}</p>
                      <p className="t-normal">{data[0].source}</p>
                    </legend>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === data[0].answer[0].name}
                        value={data[0].answer[0].name}
                        onChange={(e)=>{ setRadio1(e.target.value) 
                        console.log(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                        {data[0].answer[0].name}
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === data[0].answer[1].name}
                        value={data[0].answer[1].name}
                        onChange={(e)=>{ setRadio1(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                        {data[0].answer[1].name}
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === data[0].answer[2].name}
                        value={data[0].answer[2].name}
                        onChange={(e)=>{ setRadio1(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                        {data[0].answer[2].name}
                      </Label>
                    </FormGroup>
                  </FormGroup>
                </Col>
                <Col className="t-right">
                  <img src={A1img} className="qimage" alt="" />
                </Col>
              </Row>
            </div>

            {/* 第二問 */}
            <div className="mb-4">
              <h3 className="ttlNum mb-3"><img src={boboicon} className="Bobo-icon" alt="icon" />第2問</h3>
              <Row
                sm="2"
                xs="1"
              >
                <Col>
                  <FormGroup tag="fieldset" className="q-set">
                    <legend>
                      <p>ダイナマイト3兄弟にハジケの勝負を挑んだボーボボ！<br />初めての株でボーボボが投資した金額はいくら？</p>
                      <p className="t-normal">※出典(コミックス第１巻：奥義３／アニメ第3話より)</p>
                    </legend>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "5万"}
                        value="5万"
                        onChange={(e)=>{ setRadio1(e.target.value) 
                        console.log(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                      5万
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "Ms.メロン"}
                        value="Ms.メロン"
                        onChange={(e)=>{ setRadio1(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                        Ms.メロン
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "メロン子"}
                        value="メロン子"
                        onChange={(e)=>{ setRadio1(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                        メロン子
                      </Label>
                    </FormGroup>
                  </FormGroup>
                </Col>
                <Col className="t-right">
                  <img src={A1img} className="qimage" alt="" />
                </Col>
              </Row>
            </div>

            {/* 第三問 */}
            {/* 第四問 */}

            <Button 
            className="b-kaitou"
            onClick={selectCheck}
            >
            回答する
            </Button>

          </Form>
        </main>

        <Footer />
      </Container>
    </>
  );
}

export default Home;
