import React, {useState} from 'react';
import { Col, Container, Row, Form, FormGroup, Label, Input, Button } from 'reactstrap';
import Header from './common/Header';
import Footer from './common/Footer';
import { useHistory } from "react-router-dom";
// import quizData from "../quiz-data.json"
// import QuizList from "./QuizList";
import boboicon from '../images/icon_bomb.png';
import A1img from '../images/A1.png';

const Home = (props) => {
  const [radio1, setRadio] = useState("メロンプリンセス")
  // console.log(radio)

  let history = useHistory();
  function selectCheck(){
    if(radio1 === "メロンプリンセス"){
      history.push("/congrats")
    }else{
      history.push("/again")
    }
  }

  // const [quizs] = useState(quizData)

  return(
    <>
      <Container className="p-4 bg yellow">
        <Header />

        <main className="bg-white my-3">
          <Form>

            {/* 第一問 */}
            {/* <QuizList quizs={quizs} /> */}



            <div className="mb-4">
              <h3 className="ttlNum mb-3"><img src={boboicon} className="Bobo-icon" alt="icon" />第1問</h3>
              <Row
                sm="2"
                xs="1"
              >
                <Col>
                  <FormGroup tag="fieldset" className="q-set">
                    <legend>
                      <p>突然現れた胃液ガールズ！レモンプリンセス、イチゴプリンセス、もう一人は？</p>
                      <p className="t-normal">※出典(コミックス第7巻：奥義71／アニメ第28話より)</p>
                    </legend>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "メロンプリンセス"}
                        value="メロンプリンセス"
                        onChange={(e)=>{ setRadio(e.target.value) 
                        console.log(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                        メロンプリンセス
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "Ms.メロン"}
                        value="Ms.メロン"
                        onChange={(e)=>{ setRadio(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                        Ms.メロン
                      </Label>
                    </FormGroup>
                    <FormGroup>
                      <Input
                        type={'radio'}
                        checked={radio1 === "メロン子"}
                        value="メロン子"
                        onChange={(e)=>{ setRadio(e.target.value) }}
                        name="radioGroup1"
                      />
                      {' '}
                      <Label>
                        メロン子
                      </Label>
                    </FormGroup>
                  </FormGroup>
                </Col>
                <Col className="t-right">
                  <img src={A1img} className="qimage" alt="" />
                </Col>
              </Row>
            </div>

            {/* 第二問 */}
            {/* 第三問 */}
            {/* 第四問 */}

            <Button 
            className="b-kaitou"
            onClick={selectCheck}
            >
            回答する
            </Button>

          </Form>
        </main>

        <Footer />
      </Container>
    </>
  );
}

export default Home;
